SELECT  M_DATE_SYS, M_DATE_CMP, M_SCRPT_NAME, T2.M_OBJECT_NAME, T2.M_STEP, M_USER, M_GROUP, M_DESK,  M_TIME_CPU, M_TIME_SYB, M_TIME_ELAP FROM
(select substr(M_COMMENT, INSTR(M_COMMENT,'step')+5, INSTR(M_COMMENT, '/')- INSTR(M_COMMENT,'step') - 5) as M_STEP, M_USER, M_GROUP, M_DESK, M_SCRPT_NAME, M_DATE_SYS, M_DATE_CMP, M_TIME_CPU, M_TIME_SYB, M_TIME_ELAP from TRN_EODA_DBF where M_UNIT_NAME in ('REP_BATCHES_EXT', 'REP_BATCHES_FEED') and M_START_END='END'  and M_COMMENT  like '%step%' ) T1,
(SELECT T1.M_NAME , T2.M_ORDER+1 AS M_STEP, T2.M_PARAM_LAB2 AS M_OBJECT_NAME FROM PROCESS#PS_SCRPT_DBF T1, PROCESS#PS_ITEM_DBF T2 WHERE T1.M_REF=T2.M_REF) T2
WHERE T1.M_SCRPT_NAME=T2.M_NAME AND T1.M_STEP=T2.M_STEP