select M_CREATION,' | '
,DYNAMIC_TABLE_CATEGORY from (
 select DISTINCT T1.M_CREATION, 'Murex' as DYNAMIC_TABLE_CATEGORY from DYNDBF1#TRN_DYND_DBF T1,
(select sum(case when M_FLD_LABEL LIKE 'S\_%' escape '\' or M_FLD_LABEL LIKE 'SV\_%' escape '\'  then 1 else 0 end) as S_NUMBER,M_CREATION FROM DYNDBF1#TRN_DYNF_DBF GROUP BY M_CREATION) T2
where T1.M_CREATION=T2.M_CREATION AND T1.M_F_SVCMP=1 AND T1.M_F_CMPNO=0 AND M_CLASS LIKE 'DYN_TRNRP%' AND T2.S_NUMBER=0
union all
 select DISTINCT T1.M_CREATION, 'User' as DYNAMIC_TABLE_CATEGORY from DYNDBF2#TRN_DYND_DBF T1,
(select sum(case when M_FLD_LABEL LIKE 'S\_%' escape '\' or M_FLD_LABEL LIKE 'SV\_%' escape '\'  then 1 else 0 end) as S_NUMBER,M_CREATION FROM DYNDBF2#TRN_DYNF_DBF GROUP BY M_CREATION) T2
where T1.M_CREATION=T2.M_CREATION AND T1.M_F_SVCMP=1 AND T1.M_F_CMPNO=0 AND M_CLASS LIKE 'DYN_TRNRP%' AND  T2.S_NUMBER=0
union all
 select DISTINCT T1.M_CREATION, 'Murex Additional' as DYNAMIC_TABLE_CATEGORY from DYNDBF3#TRN_DYND_DBF T1,
(select sum(case when M_FLD_LABEL LIKE 'S\_%' escape '\' or M_FLD_LABEL LIKE 'SV\_%' escape '\'  then 1 else 0 end) as S_NUMBER,M_CREATION FROM DYNDBF3#TRN_DYNF_DBF GROUP BY M_CREATION) T2
where T1.M_CREATION=T2.M_CREATION AND T1.M_F_SVCMP=1 AND T1.M_F_CMPNO=0 AND M_CLASS LIKE 'DYN_TRNRP%' AND  T2.S_NUMBER=0
union all
 select DISTINCT T1.M_CREATION, 'User additional' as DYNAMIC_TABLE_CATEGORY from DYNDBF4#TRN_DYND_DBF T1,
(select sum(case when M_FLD_LABEL LIKE 'S\_%' escape '\' or M_FLD_LABEL LIKE 'SV\_%' escape '\'  then 1 else 0 end) as S_NUMBER,M_CREATION FROM DYNDBF4#TRN_DYNF_DBF GROUP BY M_CREATION) T2
where T1.M_CREATION=T2.M_CREATION AND T1.M_F_SVCMP=1 AND T1.M_F_CMPNO=0 AND M_CLASS LIKE 'DYN_TRNRP%' AND  T2.S_NUMBER=0
 ) TABLE1